from math import *def  haversine(lat1, lon1, lat2, lon2):        R = 6371000        lat1_r = radians(lat1)    lon1_r = radians(lon1)    lat2_r = radians(lat2)    lon2_r = radians(lon2)        dlat = lat2_r - lat1_r    dlon = lon2_r - lon1_r        hav = (sin(dlat/2) ** 2) + cos(lat1_r) * cos(lat2_r) * (sin(dlon/2) ** 2)    c = 2 * atan2(sqrt(hav), sqrt(1 - hav))        distance_km = round((R * c) / 1000, 2)         print(f"Odległość: {distance_km} km")        haversine(52.2298, 21.0118, 41.902, 12.4964)